import React, { useState } from 'react';
import './App.css';
import Person from './Person/Person';

const app = props => {
  const [ personsState, setPersonsState] = useState({persons: [
        {
          name: "Max",
          age: 28
        },
        {
          name: "Manu",
          age: 28
        },
        { 
          name: "Sophie",
          age: 23
        }
      ],
      otherState: 'some other value'
    })

    return (
      <div className="App">
        <h1>Hi, I'm a React App!</h1>
        <p> This is really working! </p>
        <button onClick={this.switchNameHandler}>Switch Name</button>
        <Person name={personsState.persons[0].name} age={personsState.persons[0].age}/>
        <Person name={personsState.persons[1].name} age={personsState.persons[1].age}>My Hobbies: Running</Person>
        <Person name={personsState.persons[2].name} age={personsState.persons[2].age}/>
      </div>
    );

  // return React.createElement('div', {className: 'App'}, React.createElement('p', null, 'This is really working!'))
}

export default App;

