import React, { useState } from 'react';
import './App.css';
import Person from './Person/Person';

const App = props => {

  const [ personsState, setPersonsState] = useState({
    persons: [
      { name: "Max", age: 28 },
      { name: "Manu", age: 28 },
      { name: "Sophie", age: 23 }
    ]
  });

  // const [otherState, setOtherState] = useState('some other state');

  // console.log(personsState, otherState)
  const switchNameHandler = (newName) => {
    // console.log('Was clicked!')
    // DON'T DO THIS: this.state.persons[0].name="Maximilian"; // warning
    setPersonsState({
      persons: [
        { name: newName, age: 28 },
        { name: "Manu", age: 28 },
        { name: "Sophie", age: 25}
      ],
      otherState: personsState.otherState
    })
  }

  const nameChangedHandler = (event) => {
    setPersonsState ({
      persons: [
        { name: 'Max', age: 28 },
        { name: event.target.value, age: 28 },
        { name: "Sophie", age: 25}
      ]
    })
  }

  // console.log(personsState)

  return (
    <div className="App">
      <h1>Hi, I'm a React App!</h1>
      <p> This is really working! </p>
      <button onClick={togglePersonsHandler}>Switch Name</button>

      <div>
        <Person 
          name={personsState.persons[0].name} 
          age={personsState.persons[0].age}/>
        <Person 
          name={personsState.persons[1].name} 
          age={personsState.persons[1].age}
          click={switchNameHandler.bind(this, "Max!")}
          changed={nameChangedHandler}>
          My Hobbies: Running
        </Person>
        <Person 
          name={personsState.persons[2].name} 
          age={personsState.persons[2].age}/>
      </div>
    </div>
   );

  // return React.createElement('div', {className: 'App'}, React.createElement('p', null, 'This is really working!'))
}

export default App;
